import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/Home.css';
import Carousel from 'react-multi-carousel';


function Home() {
    const navigate = useNavigate();

    const handleShopNow = () => {
        navigate('/products');
    };

    const carouselItems = [
        {
            title: "Rustic Floral Arrangement",
            price: "$25.99",
            desc: "Beautiful floral arrangement perfect for rustic-themed weddings.",
            image: "path-to-floral-arrangement-image.jpg",
        },
        {
            title: "Elegant Tableware Set",
            price: "$45.99",
            desc: "Sophisticated tableware for a truly elegant event.",
            image: "path-to-tableware-set-image.jpg",
        },
        {
            title: "Custom Banner",
            price: "$15.99",
            desc: "Personalized banner for your special event.",
            image: "path-to-custom-banner-image.jpg",
        },
    ];

    const responsive = {
        desktop: {
            breakpoint: { max: 3000, min: 1024 },
            items: 3,
            slidesToSlide: 1,
        },
        tablet: {
            breakpoint: { max: 1024, min: 464 },
            items: 2,
            slidesToSlide: 1,
        },
        mobile: {
            breakpoint: { max: 464, min: 0 },
            items: 1,
            slidesToSlide: 1,
        },
    };

    return (
        <div className="home-container">
            <div className="background-overlay">
                <h1 className="home-header">Welcome to Your Dream Event Planner</h1>
                <p className="home-subtitle">Discover elegant and customizable decor for weddings and events.</p>

                <Carousel
                    responsive={responsive}
                    infinite={true}
                    autoPlay={true}
                    autoPlaySpeed={3000}
                    keyBoardControl={true}
                    transitionDuration={500}
                    className="carousel-container"
                >
                    {carouselItems.map((item, index) => (
                        <div className="carousel-card" key={index}>
                            <img src={item.image} alt={item.title} className="carousel-image" />
                            <div className="carousel-card-content">
                                <h2 className="carousel-card-title">{item.title}</h2>
                                <p className="carousel-card-price">{item.price}</p>
                                <p className="carousel-card-desc">{item.desc}</p>
                                <button className="cta-button" onClick={handleShopNow}>Shop Now</button>
                            </div>
                        </div>
                    ))}
                </Carousel>
            </div>
        </div>
    );
}

export default Home;
