import React, { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../components/AuthContext';
import '../styles/ProductList.css';

function ProductList() {
    const { user } = useContext(AuthContext);
    const [products, setProducts] = useState([]);
    const [cart, setCart] = useState([]);
    const [searchTerm, setSearchTerm] = useState('');
    const [selectedCategory, setSelectedCategory] = useState('');
    const navigate = useNavigate();

    useEffect(() => {
        fetchProducts();
        if (user) {
            axios.get('http://localhost:8080/cart', { withCredentials: true })
                .then(response => setCart(response.data))
                .catch(() => setCart([]));
        }

        const updateProductsListener = () => fetchProducts();
        window.addEventListener('updateProducts', updateProductsListener);

        return () => {
            window.removeEventListener('updateProducts', updateProductsListener);
        };
    }, [user]);

    const fetchProducts = () => {
        axios.get('http://localhost:8080/products', { withCredentials: true })
            .then(response => setProducts(response.data))
            .catch(() => setProducts([]));
    };

    const handleAddToCart = (product, quantity) => {
        if (!user) {
            alert('Please log in to add items to your cart.');
            return;
        }

        if (product.inventory <= 0) {
            alert('This product is out of stock.');
            return;
        }

        axios.post('http://localhost:8080/cart', { product, quantity }, { withCredentials: true })
            .then(() => {
                setCart([...cart, { ...product, cartItemId: `${product.id}-${Date.now()}`, quantity }]);
                setProducts(products.map(p => p.id === product.id ? { ...p, inventory: p.inventory - quantity } : p));
                window.dispatchEvent(new Event('updateProducts'));
            })
            .catch(console.error);
    };

    const handleRemoveFromCart = (cartItemId) => {
        if (!user) {
            alert('Please log in to remove items from your cart.');
            return;
        }

        const productId = cart.find(item => item.cartItemId === cartItemId)?.id;

        axios.delete(`http://localhost:8080/cart?productId=${productId}`, { withCredentials: true })
            .then(() => {
                setCart(cart.filter(item => item.cartItemId !== cartItemId));
                setProducts(products.map(p => p.id === productId ? { ...p, inventory: p.inventory + 1 } : p));
                window.dispatchEvent(new Event('updateProducts'));
            })
            .catch(console.error);
    };

    const handleSearch = (event) => setSearchTerm(event.target.value);
    const handleCategorySelect = (category) => setSelectedCategory(category);
    const handleBuyNow = () => navigate('/payment');

    const filteredProducts = products.filter(product =>
        product.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
        (selectedCategory === '' || product.category === selectedCategory)
    );

    return (
        <div className="product-list-container">
            <h2 className="product-list-title">Explore Our Products</h2>
            <div className="filters">
                <input
                    type="text"
                    placeholder="Search products..."
                    value={searchTerm}
                    onChange={handleSearch}
                    className="search-bar"
                />
                <div className="category-buttons">
                    {['All', 'Rustic', 'Elegant', 'Custom'].map(category => (
                        <button
                            key={category}
                            onClick={() => handleCategorySelect(category === 'All' ? '' : category.toLowerCase())}
                            className={`category-button ${selectedCategory === category.toLowerCase() ? 'active' : ''}`}
                        >
                            {category}
                        </button>
                    ))}
                </div>
            </div>
            <div className="product-grid">
                {filteredProducts.length > 0 ? (
                    filteredProducts.map(product => (
                        <div key={product.id} className="product-card">
                            <img src={product.image} alt={product.name} className="product-image" />
                            <h3 className="product-name">{product.name}</h3>
                            <p className="product-price">${product.price.toFixed(2)}</p>
                            <p className="product-category">{product.category}</p>
                            <p className="product-inventory">In stock: {product.inventory}</p>
                            <button
                                className="add-to-cart-button"
                                onClick={() => handleAddToCart(product, 1)}
                            >
                                Add to Cart
                            </button>
                        </div>
                    ))
                ) : (
                    <p className="no-products-message">No products found.</p>
                )}
            </div>
            <h2 className="shopping-cart-title">Your Shopping Cart</h2>
            {cart.length > 0 ? (
                <div className="cart-container">
                    {cart.map(item => (
                        <div key={item.cartItemId} className="cart-item-card">
                            <img src={item.image} alt={item.name} className="cart-item-image" />
                            <div className="cart-item-details">
                                <h3 className="cart-item-name">{item.name}</h3>
                                <p className="cart-item-price">Price: ${item.price.toFixed(2)}</p>
                                <p className="cart-item-quantity">Quantity: {item.quantity}</p>
                            </div>
                            <button
                                className="cart-remove-button"
                                onClick={() => handleRemoveFromCart(item.cartItemId)}
                            >
                                Remove
                            </button>
                        </div>
                    ))}
                    <button className="buy-now-button" onClick={handleBuyNow}>
                        Proceed to Checkout
                    </button>
                </div>
            ) : (
                <div className="empty-cart-container">
                    <img
                        src="https://via.placeholder.com/300x200?text=Your+Cart+is+Empty"
                        alt="Empty Cart"
                        className="empty-cart-image"
                    />
                    <p className="empty-cart-message">Your cart is empty. Add items to your cart to get started!</p>
                    <button className="continue-shopping-button" onClick={() => navigate('/products')}>
                        Continue Shopping
                    </button>
                </div>
            )}

        </div>
    );
}

export default ProductList;
