import React, { useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../components/AuthContext';
import '../styles/UserProfile.css'; // Import the CSS file for styling

function UserProfile() {
    const { user, logout } = useContext(AuthContext);
    const navigate = useNavigate();
    const [address, setAddress] = useState('');
    const [username, setUsername] = useState(user ? user.username : '');
    const [password, setPassword] = useState(user ? user.password : '');

    useEffect(() => {
        if (!user) {
            navigate('/login');
            return;
        }

        axios.get(`http://localhost:8080/user/address?username=${user.username}`, { withCredentials: true })
            .then(response => {
                setAddress(response.data[user.username]);
            })
            .catch(error => console.error('Error fetching address:', error));
    }, [user, navigate]);

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    const handleUserUpdate = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8080/user/update', { oldUsername: user.username, newUsername: username, newPassword: password }, { withCredentials: true })
            .then(response => {
                alert('User information updated successfully!');
                // Update the user context
                logout();
                navigate('/login');
            })
            .catch(error => {
                alert('Error updating user information: ' + error.message);
            });
    };
    

    const handleAddressSubmit = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8080/user/address', { username: user.username, address }, { withCredentials: true })
            .then(response => {
                alert('Address saved successfully!');
            })
            .catch(error => {
                alert('Error saving address: ' + error.message);
            });
    };

    if (!user) {
        return null; // Render nothing if user is not authenticated
    }

    return (
        <div className="user-profile">
            <button onClick={() => navigate(-1)} className="back-button">Back</button>
            <h1>User Profile</h1>
            {user ? (
                <div>
<form onSubmit={handleUserUpdate}>
                    <label htmlFor="username">Username:</label>
                    <input
                        type="text"
                        id="username"
                        name="username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                    <label htmlFor="password">Password:</label>
                    <input
                        type="password"
                        id="password"
                        name="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit">Update User Info</button>
                </form>                    
                <form onSubmit={handleAddressSubmit}>
                        <label htmlFor="address">Delivery Address:</label>
                        <input
                            type="text"
                            id="address"
                            name="address"
                            value={address}
                            onChange={(e) => setAddress(e.target.value)}
                            required
                        />
                        <button type="submit">Save Address</button>
                    </form>
                    <button onClick={handleLogout}>Logout</button>
                    <button onClick={() => navigate('/order-history')} className="order-history-button">View Order History</button>
                </div>
            ) : (
                <p>No user information available.</p>
            )}
        </div>
    );
}

export default UserProfile;
