import React, { useContext, useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AuthContext } from '../components/AuthContext';
import '../styles/UserProfile.css'; // Import the CSS file for styling

function UserProfile() {
    const { user, logout } = useContext(AuthContext);
    const navigate = useNavigate();
    const [address, setAddress] = useState('');
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        if (!user) {
            navigate('/login');
            return;
        }

        axios.get(`http://localhost:8080/user/address?username=${user.username}`, { withCredentials: true })
            .then(response => {
                setAddress(response.data[user.username]);
            })
            .catch(error => console.error('Error fetching address:', error));

        axios.get(`http://localhost:8080/orders?username=${user.username}`, { withCredentials: true })
            .then(response => {
                setOrders(response.data);
            })
            .catch(error => console.error('Error fetching orders:', error));
    }, [user, navigate]);

    const handleLogout = () => {
        logout();
        navigate('/login');
    };

    const handleAddressSubmit = (event) => {
        event.preventDefault();
        axios.post('http://localhost:8080/user/address', { username: user.username, address }, { withCredentials: true })
            .then(response => {
                alert('Address saved successfully!');
            })
            .catch(error => {
                alert('Error saving address: ' + error.message);
            });
    };

    if (!user) {
        return null; // Render nothing if user is not authenticated
    }

    return (
        <div className="user-profile">
            <h1>User Profile</h1>
            {user ? (
                <div>
                    <p><strong>Username:</strong> {user.username}</p>
                    <p><strong>Password:</strong> {user.password}</p>
                    <form onSubmit={handleAddressSubmit}>
                        <label htmlFor="address">Delivery Address:</label>
                        <input
                            type="text"
                            id="address"
                            name="address"
                            value={address}
                            onChange={(e) => setAddress(e.target.value)}
                            required
                        />
                        <button type="submit">Save Address</button>
                    </form>
                    <button onClick={handleLogout}>Logout</button>
                    <h2>Order History</h2>
                    {orders.length === 0 ? (
                        <p>No orders found.</p>
                    ) : (
                        <ul>
                            {orders.map(order => (
                                <li key={order.id}>
                                    <p><strong>Order ID:</strong> {order.id}</p>
                                    <p><strong>Products:</strong></p>
                                    <ul>
                                        {order.products.map(product => (
                                            <li key={product.id}>{product.name} - ${product.price}</li>
                                        ))}
                                    </ul>
                                    <p><strong>Total Price:</strong> ${order.products.reduce((total, product) => total + product.price, 0)}</p>
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            ) : (
                <p>No user information available.</p>
            )}
        </div>
    );
}

export default UserProfile;
